#!/bin/bash\n\n# 哔哩哔哩字幕获取器 - 安装脚本\n\necho "🚀 开始安装哔哩哔哩字幕获取器..."\n\n# 检查 Python 版本\nif ! command -v python3 &> /dev/null; then\n    echo "❌ 错误：未找到 python3，请先安装 Python 3.6 或更高版本"\n    exit 1\nfi\n\necho "✅ Python 版本检查通过"\n\n# 安装依赖\necho "📦 安装 Python 依赖包..."\npip3 install requests certifi\n\n# 检查安装是否成功\nif [ $? -eq 0 ]; then\n    echo "✅ 依赖包安装成功"\nelse\n    echo "❌ 依赖包安装失败"\n    exit 1\nfi\n\n# 创建配置文件\nif [ ! -f "config.json" ]; then\n    echo "📄 创建配置文件..."\n    cp config.json.example config.json\n    echo "✅ 配置文件已创建：config.json"\n    echo "⚠️  请编辑 config.json 文件，填入你的 B站 Cookie 信息"\nelse\n    echo "⚠️  配置文件已存在，跳过创建"\nfi\n\n# 创建输出目录\nmkdir -p subtitles\n\necho "🎉 安装完成！"\necho ""\necho "📝 使用说明："\necho "1. 编辑 config.json 文件，填入 B站 Cookie 信息"\necho "2. 运行 'python3 bili_simple.py search \"关键词\"' 搜索视频"\necho "3. 运行 'python3 bili_simple.py download BV号' 下载字幕"\necho ""\necho "📖 更多信息请查看 README.md 文件"